mapper.select:
select num, no ,cer_status,cer_thumbnail,cer_content, cer_date , nickname , cer_hit, cer_like from( select rownum num, no ,cer_status,cer_thumbnail,cer_content, cer_date , nickname , cer_hit, cer_like from ( select cer.no no,cer_status,cer_thumbnail,cer_content, cer_date , nickname , cer_hit, cer_like from (select cer.no,cer_status,cer_thumbnail,cer_content, cer_date , nickname , cer_hit, cer_like from certification_board cer, member m where m.no=cer.member_no
and ${searchColumn} like '%'||#{keyword}||'%'
) cer left outer join img i on i.cer_no = cer.no order by CER_DATE))
