mapper.select:
select no,one_title,one_content,one_pwd,one_date,ord_no,member_no,one_type_code,qna_status_code from
(select rownum num, no,one_title,one_content,one_pwd,one_date,ord_no,member_no,one_type_code,qna_status_code from one)
where num between #{start} and #{end}

select no,one_title , ORD_NO, ONE_DATE, one_type_name, qna_type_name from one o, one_type ot, qna_status qs where
one_type_name = (select one_type_name from one_type where one_type_code='${searchColumn}') and
o.qna_status_code=qs.qna_status_code and member_no =#{member_no}

select no,one_title,one_content,one_date from ( select rownum num, no,one_title,one_content,one_date from (select no,one_title,one_content,one_date from one
where one_type_code='${searchColumn}'
where one_title like '%' || #{keyword} || '%' OR one_content like '%' || #{keyword} || '%'
)) where num between #{start} and #{end}
