mapper.insert:
INSERT INTO orders( no, ord_id, ord_date, ord_use_point, ord_price, payment_code, ord_status_code, apply_num,imp_uid
, address_no
, receiver_no
, member_no
) VALUES ( seq_orders.nextval, #{ord_id}, sysdate, #{ord_use_point}, #{ord_price}, #{payment_code}, #{ord_status_code}, #{apply_num}, #{imp_uid}
, #{address_no}
, #{receiver_no}
, #{member_no}
)

mapper.select:
SELECT ord_id, ord_price, apply_num , a.addr_postal as addr_postal, a.addr_road as addr_road, a.addr_detail as addr_detail, a.addr_phone as addr_phone FROM orders o
, (SELECT no, addr_postal, addr_road, addr_detail,addr_phone FROM address) a
, (SELECT no, receiver_postal as addr_postal , receiver_addr as addr_road, receiver_detail as addr_detail, receiver_phone as addr_phone FROM receiver) a
WHERE ord_id= #{ord_id}
and a.no = o.address_no and o.address_no = #{addr_no}
and a.no = o.receiver_no and o.receiver_no = #{receiver_no}
